import React from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Index from './Index';

export default class App extends React.Component{
  constructor(props){
    super(props);

    this.state = {
      city: null,
      date: null,
      time: null,
      error: null,
      isLoaded: false,
      response: null,
    }

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e){
    e.preventDefault();
    console.log(this.state)
  }

  componentDidMount(){
    fetch('http://api.openweathermap.org/data/2.5/forecast?q=Minsk&APPID=6132c3489f0e8f14f8be55cd68ff7506')
      .then(response => {
        return response.json();
      },
      error => {
        this.setState({
          isLoaded: true,
          error
        });
      })
      .then(JsonResponse => {
        this.setState({
          isLoaded: true,
          response: JsonResponse
        })
      })
  }

  render(){
    const {error, isLoaded, response} = this.state;

    if (error){
      return <h1>Error: {error.message}</h1>
    }
    else if (!isLoaded){
      return <div className='text-center'><div className="spinner-border text-info"></div></div>
    }
    else{
      return (
        <Router>
          <div>
            <h1 className='text-center display-4'>Week weather forecast</h1>
            <div className="input-group">
              <div className="input-group-prepend">
                <span className="input-group-text">City, date and time</span>
              </div>
              <input type="text" aria-label="city" className="form-control" onChange={(e) => this.setState({city: e.target.value})}/>
              <input type="text" aria-label="date" className="form-control" onChange={(e) => this.setState({date: e.target.value})}/>
              <input type="text" aria-label="time" className="form-control" onChange={(e) => this.setState({time: e.target.value})}/>
            </div>
            <button type="button" className="btn btn-primary btn-block mt-4" onClick={this.handleClick}>Find!</button>
            {/* <Route path="/" exact render={() => <Index data={response} />} /> */}
          </div>
        </Router>
      )
    }
  }
}
