import React from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Card from './Card';
import sun from '../images/sun.png';
import rain from '../images/rain.png';

const currentWeather = {
  wheatherImage: sun,
  dayTemperature: '20℃',
  nightTemperature: '15℃'
}

const otherWeather = {
  wheatherImage: rain,
  dayTemperature: '15℃',
  nightTemperature: '9℃'
}

export default class App extends React.Component{
  render(){
    function Index() {
      return (
        <div className='card-group'>
          <Card currentWeather={currentWeather} day='Monday'/>
          <Card currentWeather={otherWeather} day='Tuesday'/>
          <Card currentWeather={currentWeather} day='Wednesday'/>
          <Card currentWeather={otherWeather} day='Thursday'/>
          <Card currentWeather={currentWeather} day='Friday'/>
        </div>
      );
    }
    function Day(props){
      <Card currentWeather={currentWeather} day={props.match.params.day}/>
      return <h2>Day: {props.match.params.day}</h2>
    }
    return (
      <Router>
        <div>
          <nav>
            <ul className='list-group list-group-horizontal'>
              <Link to="/" className='list-group-item list-group-item-action list-group-item-primary'>show them all!</Link>
              <Link to="/monday/" className='list-group-item list-group-item-action'>monday</Link>
              <Link to="/tuesday/" className='list-group-item list-group-item-action'>tuesday</Link>
              <Link to="/wednesday/" className='list-group-item list-group-item-action'>wednesday</Link>
              <Link to="/thursday/" className='list-group-item list-group-item-action'>thursday</Link>
              <Link to="/friday/" className='list-group-item list-group-item-action'>friday</Link>
            </ul>
          </nav>

          <Route path="/" exact component={Index} />
          <Route path="/:day(\w+)/" component={Day} exact />
        </div>
      </Router>
    )
  }
}
