import React from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Index from './Index';

export default class App extends React.Component{
  constructor(props){
    super(props);

    this.state = {
      isComponentActive: false,
      city: '',
      error: null,
      isLoaded: false,
      response: null
    }

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e){
    e.preventDefault();

    const {city, date, time} = this.state;
    const url = `http://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=6132c3489f0e8f14f8be55cd68ff7506`;

    this.setState({
      isLoaded: false
    })

    fetch(url)
      .then(response => {
        return response.json();
      },
      error => {
        this.setState({
          isLoaded: true,
          error,
          isComponentActive: true
        });
      })
      .then(JsonResponse => {
        this.setState({
          isLoaded: true,
          response: JsonResponse,
          isComponentActive: true
        })
      })
  }

  render(){
    return (
      <Router>
        <div>
          <h1 className='text-center display-4'>Day weather forecast</h1>
          <div className="input-group">
            <div className="input-group-prepend">
              <span className="input-group-text">City, date and time</span>
            </div>
            <input type="text" aria-label="city" className="form-control" onChange={(e) => this.setState({city: e.target.value})} value={this.state.city}/>
          </div>
          <button type="button" className="btn btn-primary btn-block mt-4" onClick={this.handleClick}>Find!</button>
          {this.state.isComponentActive && 
            // <Route path="/" exact render={() => <Index data={this.state} />} />
            <Index data={this.state} />
          }
        </div>
      </Router>
    )
  }
}
