import React from 'react';
import Card from './Card';

export default class Index extends React.Component{
  constructor(props){
    super(props);

    this.state = {
      error: null,
      isLoaded: false,
      response: null
    }
  }

  componentDidMount(){
    const url = `http://api.openweathermap.org/data/2.5/forecast?q=${this.props.city}&APPID=6132c3489f0e8f14f8be55cd68ff7506`;

    fetch(url)
      .then(response => {
        return response.json();
      },
      error => {
        this.setState({
          isLoaded: true,
          error
        });
      })
      .then(JsonResponse => {
        this.setState({
          isLoaded: true,
          response: JsonResponse
        })
      })
  }

  render(){
    const {error, isLoaded, response} = this.state;

    if (error){
      return <h1>Error: {error.message}</h1>
    }

    else if (!isLoaded){
      return <div className='text-center'><div className="spinner-border text-info"></div></div>
    }

    else{
      const cards = response.list.map((item, i) => 
        <Card key={i} data={item} />
      )
      return (
        <div className='card-group'>
          {cards}
        </div>
      );
    }
  }
    
  }
