import React from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
// import Card from './Card';
import Index from './Index';

export default class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      response: null
    }
  }

  componentDidMount(){
    fetch('http://api.openweathermap.org/data/2.5/forecast?q=Minsk&APPID=6132c3489f0e8f14f8be55cd68ff7506')
      .then(response => {
        return response.json();
      },
      error => {
        this.setState({
          isLoaded: true,
          error
        });
      })
      .then(JsonResponse => {
        this.setState({
          isLoaded: true,
          response: JsonResponse
        })
      })
  }

  render(){
    const {error, isLoaded, response} = this.state;

    if (error){
      return <h1>Error: {error.message}</h1>
    }
    else if (!isLoaded){
      return (
        <div class="row h-50">
            <div class="col-sm-12 h-100 d-table">
                <div class="card card-body d-table-cell align-middle">
                    I am centered
                </div>
            </div>
        </div>
      )
    }
    else{
      return (
        <Router>
          <div>
            <Route path="/" exact render={() => <Index data={response} />} />
            {/* <Route path="/" exact render={() => <Card data={response.list[0]} />} /> */}
          </div>
        </Router>      
      )
    }
  }
}
