import React from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Card from './Card';
// import Index from './Index';

export default class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      response: null
    }
  }

  componentDidMount(){
    fetch('http://api.openweathermap.org/data/2.5/forecast?q=Minsk&APPID=6132c3489f0e8f14f8be55cd68ff7506')
      .then(response => {
        return response.json();
      },
      error => {
        this.setState({
          isLoaded: true,
          error
        });
      })
      .then(JsonResponse => {
        this.setState({
          isLoaded: true,
          response: JsonResponse
        })
      })
  }

  render(){
    const {error, isLoaded, response} = this.state;

    if (error){
      return <h1>Error: {error.message}</h1>
    }
    else if (!isLoaded){
      return <h1 className='text-center'>Loading..</h1>
    }
    else{
      return (
        <Router>
          <div>
            {/* <nav>
              <ul className='list-group list-group-horizontal'>
                <Link to="/" className='list-group-item list-group-item-action list-group-item-primary'>show them all!</Link>
                <Link to="/monday/" className='list-group-item list-group-item-action'>monday</Link>
                <Link to="/tuesday/" className='list-group-item list-group-item-action'>tuesday</Link>
                <Link to="/wednesday/" className='list-group-item list-group-item-action'>wednesday</Link>
                <Link to="/thursday/" className='list-group-item list-group-item-action'>thursday</Link>
                <Link to="/friday/" className='list-group-item list-group-item-action'>friday</Link>
              </ul>
            </nav> */}

            {/* <Route path="/" exact render={() => <Index data={response} />} /> */}
            <Route path="/:day(\w+)/" exact render={() => <Card data={response} />} />
          </div>
        </Router>      
      )
    }
  }
}
