import React from 'react';
import '../styles/index.css';
import sun from '../images/sun.png';
import rain from '../images/rain.png';
import cloud from '../images/cloud.png';
import wtf from '../images/eclipse.png';

export default class Card extends React.Component{
    render(){
        const {main: {temp_min, temp_max, pressure, humidity}, visibility, weather, dt_txt: date_time} = this.props.data;

        const weatherDescription = weather[0].main;
        var img;
        switch (weatherDescription) {
            case 'Clear':
                img = sun;
                break;
            case 'Clouds':
                img = cloud;
                break;
            case 'Rain':
                img = rain;
                break;
            default:
                img = wtf;
                break;
        }

        return (
            <div className="card text-center m-2 flex-fill">
                <div className='card-header'>
                    <div className='card-text'>Date: {date_time.split(' ')[0]}</div>
                    <div className='card-text'>Time: {date_time.split(' ')[1]}</div>
                    <div className='card-text'>Weather: {weatherDescription}</div>
                </div>
                <div className="card-body">
                    <img src={img} className="card-img" alt="..." />
                </div>
                <div className="card-footer">
                    <div className='card-text'>
                        Max: <span>{ Math.floor(temp_max - 273) + '℃'} </span>
                    </div>
                    <div className='card-text text-muted'>
                        Min: <span>{ Math.floor(temp_min - 273) + '℃'} </span>
                    </div>
                </div>    
            </div>
        )
    }
}
