import React from 'react';
import '../styles/index.css';
import sun from '../images/sun.png';
import rain from '../images/rain.png';

export default class Card extends React.Component{
    render(){
        const {main: [temp_min, temp_max], weather} = this.props.data;
        const weatherDescription = weather[0].main;
        let img;
        if(weatherDescription === 'Clear') img = sun;
        else img = rain;
        console.log(this.props.data)
        // this.props.data.list.forEach((element, i) => {
        //     let [date, time] = element.dt_txt.split(' ');
        //     console.log(date, time, i)
        // });
        return (
            <div className="card text-center m-2">
                <div className='card-header text-muted'>
                    <div className='card-text'>City: -</div>
                    <div className='card-text'>Coords: -</div>
                    <div className='card-text'>Weather: {weatherDescription}</div>
                </div>
                <div className="card-body">
                    <img src={img} className="card-img" alt="..." />
                </div>
                <div className="card-footer">
                    <div className='card-text'>
                        Max: <span>{ Math.floor(temp_max - 273) + '℃'} </span>
                    </div>
                    <div className='card-text text-muted'>
                        Min: <span>{ Math.floor(temp_min - 273) + '℃'} </span>
                    </div>
                </div>    
            </div>
        )
    }
}
