import React from 'react';
import '../styles/index.css';
import sun from '../images/sun.png';
import rain from '../images/rain.png';

export default class Card extends React.Component{
    render(){
        const {city: {name, coord}, list: [currentWeather]} = this.props.data;
        const weatherDescription = currentWeather.weather[0].main
        let img;
        if(weatherDescription === 'Clear') img = sun;
        else img = rain;
        return (
            <div className="card text-center m-2">
                <div className='card-header text-muted'>
                    <div className='card-text'>City: {name}</div>
                    <div className='card-text'>Coords: {coord.lat}, {coord.lon}</div>
                    <div className='card-text'>Weather: {weatherDescription}</div>
                </div>
                <div className="card-body">
                    <img src={img} className="card-img" alt="..." />
                </div>
                <div className="card-footer">
                    <div className=''></div>
                    <span className="card-text">{ Math.floor(currentWeather.main.temp_max - 273) + '℃'} </span>
                    <span className="card-text text-muted">{ Math.floor(currentWeather.main.temp_min - 273) + '℃' }</span>
                </div>    
            </div>
        )
    }
}
